(function () {
		'use strict';
		//get external services
		var eventUUID = 'B8164CA929A34E88BE5EC5B5DA75C13E';
		var basicsWorkflowEventService = $injector.get('basicsWorkflowEventService');
		var basicsWorkflowInstanceService = $injector.get('basicsWorkflowInstanceService');
		var reportingPlatformService = $injector.get('reportingPlatformService');
		var basicsCommonFileDownloadService = $injector.get('basicsCommonFileDownloadService');
		var documentDownloadPreviewButtonService = $injector.get('documentDownloadPreviewButtonService');


		var $timeout = $injector.get('$timeout');
		var $translate = $injector.get('$translate');
		if (angular.isString($scope.Context)) {
			$scope.Context = angular.fromJson($scope.Context);
		}
		//init check boxes
		$scope.sendAsBccOptions = {
			ctrlId: 'sendAsBccOptions',
			labelText: $translate.instant('basics.workflow.sendRfQDialog.clerkEmailInBcc')
		};
		$scope.sendFromMeOptions = {
			ctrlId: 'SendFromMeOptions',
			labelText: $translate.instant('basics.workflow.sendRfQDialog.sendWithOwn')
		};
		$scope.generateSafeLinkOptions = {
			ctrlId: 'generateSafeLinkOptions',
			labelText: $translate.instant('basics.workflow.sendRfQDialog.generateSafeLink')
		};
  		$scope.disableDataFormatExportOptions = {
			ctrlId: 'disableDataFormatExportOptions',
			labelText: $translate.instant('basics.workflow.sendRfQDialog.disableDataFormatExport')
		};
		$scope.button = {};
		var automaticPlaceholder = {
			CompanyID: 'Context.CompanyId',
			RfqHeaderID: 'Context.Entity.Id',
			RFQID: 'Context.Entity.Id',
			RfqID: 'Context.Entity.Id',
			Link: 'Context.Url',
			UserID: 'Context.UserId',
			PreviewUICulture: 'Context.Culture',
			ReqID: '',
			BidderID: 'Context.Bidder.BidderId',
			UI_Language: 'Context.Culture',
			BusinessPartnerID: 'Context.Bidder.BidderId'
		};// init list functions
		$scope.info = {
			selectedBidder: [],
			selectedReportsAll: 0,
			selectedMandetoryReports: 0,
			selectedReports: 0,
			get selectedCoverLetter() {
				var result = _.find($scope.Context.CoverLetterList, function (item) {
					return item.ReportID == $scope.Context.SelectedBodyLetter;
				});
				if (!result) {
					result = $scope.Context.CoverLetterList[0];
				}
				return result;
			},
			get selectedDataFormat() {
				return _.find($scope.Context.DataFormatList, function (item) {
					return item.DataformatKey == $scope.Context.SelectedDataFormat;
				});
			},
			get selectedDokuments() {
				return _.concat($scope.info.selectedProjectDocs, $scope.info.selectedReQDocs, $scope.info.selectedRfQDocs, $scope.info.selectedClerkDocs);
			},
			get selectedProjectDocs() {
				return _.filter($scope.Context.ProjectDocuments, {Selected: true});
			},
			get selectedReQDocs() {
				return _.filter($scope.Context.ReQDocuments, {Selected: true});
			},
			get selectedRfQDocs() {
				return _.filter($scope.Context.RfQDocuments, {Selected: true});
			},
			get selectedClerkDocs() {
				return _.filter($scope.Context.ClerkDocuments, {Selected: true});
			},
			selectedPlaceholder: []
		};// functions to prepare documents and reports

		function prepareDocList(list) {
			if (list) {
				for (var index = 0; index < list.length; index++) {
					list[index].Options = {"displayText": list[index].OriginFileName, "documentId": list[index].Id};
				}
			} else {
				list = [];
			}
		}

		function prepareReportList(list) {
			for (var index = 0; index < list.length; index++) {
				list[index].Options = {
					"displayText": '<i class="block-image control-icons ico-preview"></i>',
					"report": {
						"Id": list[index].ReportID,
						"Name": list[index].Description,
						"TemplateName": list[index].TemplateName,
						"Path": list[index].TemplatePath
					},
					"parameters": list[index].Parameters
				};
			}
		}

		function prepareContacts(bidderList) {
			for (var index = 0; index < bidderList.length; index++) {
				if (bidderList[index]) {
					bidderList[index].contactOptions = {items: [], displayMember: 'Role', valueMember: 'Email'};
					if (bidderList[index].ContactLookup) {
						bidderList[index].contactOptions.items = bidderList[index].ContactLookup;
					}
				}
			}
		}

//prepare the lists
		prepareDocList($scope.Context.ProjectDocuments);
		prepareDocList($scope.Context.ReQDocuments);
		prepareDocList($scope.Context.RfQDocuments);
		prepareReportList($scope.Context.ReportList);
		prepareContacts($scope.Context.BidderList);
		$scope.changeContact = function (bidder) {
			var contact = _.find(bidder.ContactLookup, {Email: bidder.ContactEmail});
			bidder.ContactFirstName = contact.FirstName;
			bidder.ContactLastName = contact.LastName;
		};
		$scope.openReport = function openReport(id) {
			var report = _.find($scope.Context.ReportList, {ReportID: id});
			if (!report) {
				report = _.find($scope.Context.MandentoryReportList, {ReportID: id});
				if (!report) {
					report = _.find($scope.Context.CoverLetterList, {ReportID: id});
				}
			}
			if (report) {
				var tempParameters = angular.copy(report.Parameter);
				_.forEach(tempParameters, function (param) {
					if (param.DataType === 'System.String') {
						if (!angular.isString(param.ParamValue) || (angular.isString(param.ParamValue) && !param.ParamValue.includes('"'))) {
							param.ParamValue = '"' + param.ParamValue + '"';
						}
					}
				});
				var reportObj = {
					"Id": report.ReportID,
					"Name": report.Description,
					"TemplateName": report.FileName,
					"Path": report.TemplatePath
				};
				//replace parameter variables
				reportingPlatformService.prepare(reportObj, tempParameters, 'pdf').then(function (response) {
					reportingPlatformService.show(response);
				});
			}
		};

		//Updated on 18.03.2020 - Document would open in a new Window, instead of downloading.
		$scope.openDocument = function openDocument(docId) {
			documentDownloadPreviewButtonService.prepareDocPreviewById(docId);
		};

		//Send
		$scope.send = function () {
			$timeout(function () {
				$('#tabrfqbiddertab5').trigger('click');
			});
			$scope.button.disabled = true;
			var paramsContext = {
				"CommunicationChannel": $scope.Context.CommunicationChannel,
				"SelectedBodyLetter": $scope.Context.SelectedBodyLetter,
				"SelectedBodyLetterParameters": $scope.info.selectedCoverLetter.Parameter,
				"ReportList": $scope.info.selectedReportsAll,
				"DocumentList": _.map($scope.info.selectedDokuments, function (item) {
					return item.hasOwnProperty('Id') ? item.Id : item.DocId;
				}),
				"StartingClerk": $scope.Context.StartingClerk,
				"Subject": $scope.Context.Subject,
				"SendAsBCC": $scope.Context.SendAsBCC,
				"SendFromMe": $scope.Context.SendFromMe,
				"GenerateSafeLink": $scope.Context.GenerateSafeLink,
                "DisableDataFormatExport": $scope.Context.DisableDataFormatExport,
				"selectedDataFormat": $scope.info.selectedDataFormat,
				"Url": window.location.href.substr(0, window.location.href.indexOf('/#'))
			};

           function setBidderInReport(context, bidder)
          {
              if(context.ReportList){
                  _.each(context.ReportList, function (report) {
                      _.each(report.Parameter, function (parameter) {
                          if(parameter.ParameterName === 'BidderID'){
                              parameter.ParamValue = bidder.BidderId;
                          }
                      });
                  });
              }
          }

			$scope.errorOccured = false;
			$scope.noValidConfiguration = false;
			var generateSafeLink = paramsContext.GenerateSafeLink;
			var isLetterForSafeLink = paramsContext.SelectedBodyLetterParameters.some(function (param) {
				return param.ParameterName === "Link";
			});
			$scope.safeLinkFailed = generateSafeLink === true && isLetterForSafeLink === false || generateSafeLink === false && isLetterForSafeLink === true;
			if(!$scope.safeLinkFailed) {
				_.forEach($scope.info.selectedBidder, function (bidder) {
					bidder.SendStatus = -1;
					var bidderContext = {"Bidder": bidder};
              		setBidderInReport(paramsContext, bidder);
					var jsonContext = angular.toJson(_.merge(paramsContext, bidderContext));
					basicsWorkflowEventService.startWorkflow(eventUUID, $scope.Context.Entity.Id, jsonContext).then(function (response) {
						if (!_.isEmpty(response)) {
							var status = response.Status;
							if (status && status === 5) {
								$scope.button.disabled = false;
								$scope.errorOccured = true;
								$scope.errorWhileSending = $translate.instant('basics.workflow.errorRfQ.errorWhileSendingEMail');
							}
							bidder.SendStatus = status;
						} else {
							$scope.button.disabled = false;
							bidder.SendStatus = 1;
						}
					});
				});
			} else {
				$scope.button.disabled = false;
				$scope.errorInConfiguration = $translate.instant('basics.workflow.errorRfQ.wrongReportSelectedError');
			}
		};
		$scope.selectedBodyLetterChanged = function () {
			if ($scope.info.selectedCoverLetter) {
				$scope.info.selectedPlaceholder = _.filter($scope.info.selectedCoverLetter.Parameter, function (item) {
					return !automaticPlaceholder.hasOwnProperty(item.ParameterName);
				});
			}
		};
		$scope.selectedBidderChanged = function () {
			$scope.info.selectedBidder = _.filter($scope.Context.BidderList, {Selected: true});
		};
		$scope.selectedReportsChanged = function () {
			$scope.info.selectedReports = _.filter($scope.Context.ReportList, {Selected: true});
			$scope.info.selectedReportsAll = _.concat(_.filter($scope.Context.MandentoryReportList, {Selected: true}), _.filter($scope.Context.ReportList, {Selected: true}));
		};
		$scope.bccChanged = function () {
			$scope.Context.SendAsBCC != $scope.Context.SendAsBCC;
		};
		$scope.sendMeChanged = function () {
			$scope.Context.SendFromMe != $scope.Context.SendFromMe;
		};
		$scope.safeLinkChanged = function () {
			$scope.Context.GenerateSafeLink != $scope.Context.GenerateSafeLink;
		};
  		$scope.disableDataFormatExportChanged = function () {
			$scope.Context.DisableDataFormatExport != $scope.Context.DisableDataFormatExport;
		};
		$scope.Context.SendAsBCC = false;
		$scope.Context.SendFromMe = false;
		$scope.Context.GenerateSafeLink = false;
  		$scope.Context.DisableDataFormatExport = false;
		$scope.selectedBidderChanged();
		$scope.selectedReportsChanged();
		$scope.selectedBodyLetterChanged();
	}
)();
