(function () {
    'use strict';
    /*global angular*/

    var moduleName = 'productionplanning.common';
    angular.module(moduleName).factory('ppsCommonLogLayout', logLayout);
    logLayout.$inject = [];
    function logLayout() {
        var attrs = ['date', 'ppsitemcode', 'ppsentitydescription', 'recordcode', 'recorddescription', 'recordtype', 'columnname',
            'oldvaluedescription', 'newvaluedescription', 'reason',
            'remark','updater', 'autogenerated', 'recordid'];
        var config = {
            fid: 'productionplanning.common.ppsLogLayout',
            version: '1.0.0',
            showGrouping: false,
            addValidationAutomatically: true,
            groups: [
                {
                    gid: 'baseGroup',
                    attributes: attrs
                }
            ],
            overloads: { }
        };

        attrs.forEach(function (attr) {
            config.overloads[attr] = {grid: {width:80}};
        });

        return config;
    }
})();